style.main:
 Add padding space */
main#cwf-content {
    padding-bottom: 3rem !important;
}
/****************************************/
/* Transitions */
.hp-transition--ease-in-out {
    -webkit-transition: var(--hp-transition--ease-in);
    -moz-transition: var(--hp-transition--ease-in);
    -o-transition: var(--hp-transition--ease-in);
    transition: var(--hp-transition--ease-in);
}
/****************************************/
/* Typography styles */
/* Color - Text headers in #cwf-content */
#cwf-content :is(h2,h3):not(
    .hp-billboard :where(h2,h3), 
    .hp-stats-list :where(h2,h3), 
    .hp-card-reveal :where(h2,h3), 
    .hp-sxsvideo :where(h2,h3), 
    .hp-verticalvideos :where(h2,h3), 
    .hp-people-profiles-title :where(h2,h3), 
    .hp-card--img-stacked :where(h2,h3), 
    .hp-fact :where(h2,h3)) {
    color: var(--hp-color--orange);
}
/* Set paragraph font weight */
#cwf-content p {
    font-weight: 300;
}
/*********************************************/
/* Why VCU - text link styles */
/* Why VCU default - .section */
:is(.thrive-slide, .financialGuidance) a,
/* main site default - #cwf-content */
#cwf-content :is(a,a:active,a:link):not(
    .hp-billboard :where(a,a:active,a:link), 
    .hp-stats-list :where(a,a:active,a:link), 
    .hp-card-reveal :where(a,a:active,a:link), 
    .hp-cta :where(a,a:active,a:link), 
    .hp-sxsvideo :where(a,a:active,a:link), 
    .hp-verticalvideos :where(a,a:active,a:link), 
    .hp-people-profiles-title :where(a,a:active,a:link), 
    .hp-fact :where(a,a:active,a:link)) {
        /*color: var(--hp-color--orange);*/
        cursor: pointer;
        text-decoration: none;
        border-bottom: 1px solid;
        border-color: var(--hp-color--gold);
        padding-bottom: 2px;
        color: var(--hp-color--black);
        font-weight: 700;
}
/* Why VCU default - .section */
:is(.thrive-slide, .financialGuidance) :is(a:hover,a:focus),
/* main site default - #cwf-content */
#cwf-content :is(a:hover,a:focus):not(
    .hp-billboard :where(a:hover,a:focus), 
    .hp-stats-list :where(a:hover,a:focus), 
    .hp-card-reveal :where(a:hover,a:focus), 
    .hp-cta :where(a:hover,a:focus), 
    .hp-sxsvideo :where(a:hover,a:focus), 
    .hp-verticalvideos :where(a:hover,a:focus), 
    .hp-people-profiles-title :where(a:hover,a:focus), 
    .hp-fact :where(a:hover,a:focus)) {
        text-decoration: none;
        color: var(--hp-color--orange);
        border-color: transparent;
}
/* Why VCU - black bg text links */
:is(.wgyb, #A-unique-combination, #Set-you-apart) a {
    border-color: var(--hp-color--gold) !important;
    color: var(--hp-color--gold) !important;
}
:is(.wgyb, #A-unique-combination, #Set-you-apart) a:active,
:is(.wgyb, #A-unique-combination, #Set-you-apart) a:hover,
:is(.wgyb, #A-unique-combination, #Set-you-apart) a:focus {
   color: var(--hp-color--white) !important;
   border-color: transparent !important;
}
@media (min-width: 1024px) {
    :is(#Test-Market) a {
    border-color: var(--hp-color--gold) !important;
    color: var(--hp-color--gold) !important;
}
    :is(#Test-Market) a:active,
    :is(#Test-Market) a:hover,
    :is(#Test-Market) a:focus {
       color: var(--hp-color--white) !important;
       border-color: transparent !important;
    }
}
/* Why VCU - gold bg text links */
:is(#Champion-of-diversity) a {
    border-color: var(--hp-color--black) !important;
    color: var(--hp-color--black) !important;
}
:is(#Champion-of-diversity) a:active,
:is(#Champion-of-diversity) a:hover,
:is(#Champion-of-diversity) a:focus {
   color: var(--hp-color--orange) !important;
   border-color: transparent !important;
}
/*********************************************/
/* Text color classes */
.hp-text--white {color:var(--hp-color--white) !important;}
.hp-text--gold {color:var(--hp-color--gold) !important;}
.hp-text--black {color:var(--hp-color--black) !important;}
/*********************************************/
/* Text case */
.hp-text--uppercase {text-transform: uppercase !important;}
/*********************************************/
/* Videos embeded in general content type */
/* Example is on the Athletics page */
.video-embed--wrapper {
    position: relative;
    padding-bottom: 56.25%;
 /* 16:9 */
    height: 0;
}
.video-embed--wrapper  iframe {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
}
/*********************************************/
/* Button styles */
.cwf-button,
.t4_button {
    --hp-button--background-color: var(--hp-color--gold);
    --hp-button--border-color: var(--hp-color--gold);
    --hp-button--color: #121212;
    --hp-button--active--background-color: var(--hp-color--gold);
    --hp-button--active--color: #fff;
    --hp-button--hover-focus--background-color: #212121;
    --hp-button--hover-focus--color: #fff;
}
.cwf-button,
a.t4_button,
.t4_button a, 
.t4_button button,
.t4_button input[type=button], 
.t4_button input[type=reset], 
.t4_button input[type=submit] {
    border-radius: 1.5rem;
    display: inline-block;
    font-family: var(--cwf-font--sans-serif,inherit);
    font-weight: 700;
    line-height: 1.5;
    padding: 0.5rem 1rem !important;
    text-decoration: none;
    transition: background-color .1s ease-in-out,color .1s ease-in-out;
    -webkit-user-select: none;
    -moz-user-select: none;
    user-select: none;
}
/* Note 8.31.23 - Conflicts with accessibility changes RO */
#cwf-content :is(a.t4_button,.t4_button a):not(.hp-billboard a.t4_button) {
    background-color: var(--hp-button--background-color)!important;
    border: 1px solid var(--hp-button--border-color);
    color: var(--hp-button--color)!important;
}
#cwf-content :is(a.t4_button:hover,a.t4_button:active,a.t4_button:focus):not(.hp-billboard a.t4_button) {
    background-color: var(--hp-button--hover-focus--background-color)!important;
    border: 1px solid var(--hp-button--border-color);
    color: var(--hp-button--hover-focus--color)!important;
}
/* Add icon */
p.t4_button:after,
a.t4_button:after,
.t4_button button:after {
    font-family: FontAwesome;
    font-weight: 400;
    line-height: 1;
    content: "\f105";
    display: inline-block;
    margin: -2px 0 0;
    padding: 0;
    padding-left: 8px;
    vertical-align: middle;
}
/****************************************/
/* Multi-column UL */
/* Create a two column unordered list */
@media (min-width:800px) {
    ul.list--2-columns {
        -webkit-column-count: 2;  /* Chrome/Opera, Safari */
        -moz-column-count: 2; /* Mozilla Firefox */
        column-count: 2;
        /* Properties below are optional: */
        -webkit-column-gap: 50px; /* Chrome/Opera, Safari */
        -moz-column-gap: 50px; /* Mozilla Firefox */
        column-gap: 50px;
    }
}
/****************************************/
/* Full-width option for content blocks */
/* Create full-width blocks using the .hp-full-width class */
.hp-full-width {
    width: 100vw;
    position: relative;
    left: 50%;
    right: 50%;
    /* add half of main's left pad to the midpoint */
    margin-left: calc(-50vw - 0.5rem);
    /* push content back on screen */
    padding-left: 1rem;
 }
/* Full-width blocks -- If there is a sub nav, modify margin */
@media (min-width:1024px) {
  :is(.cwf-grid__column--page-layout--subnav+main.cwf-grid__column--page-layout--main .hp-full-width) {
        /* margin-left: calc(-50vw - 160px - 1rem); */
        margin-left: calc(-50vw - 151.5px + 1rem);
   }
}
/****************************************/
/* Hero image header on sub-pages */
/* Using the Marcom - Multimedia Hero */
/* Use 'hero-feature-subpage' in T4 name field */
/* Reset any inherited defaults */
#hp-feature-subpage .owl-item.active .marcom-hero .content,
#hero-feature-subpage.marcom-hero,
#hero-feature-subpage .marcom-hero-overlay,
#hero-feature-subpage .content .textTitle,
#hero-feature-subpage .textTitle p,
#hero-feature-subpage .textTitle h1,
#hero-feature-subpage .textTitle h2, 
#hero-feature-subpage .content .inner,
#hero-feature-subpage.marcom-hero .content {all:unset;all:revert;}
/* Hero - subpage title + image */
#hero-feature-subpage.marcom-hero {
  --hero-feature-subpage--background-color: #fab300;
  --hero-feature-subpage--foreground-color: #121212;
}
#hero-feature-subpage.marcom-hero img {
  border-bottom: 5px solid var(--hero-feature-subpage--background-color);
}
#hero-feature-subpage.marcom-hero .content {
  background: var(--hero-feature-subpage--background-color);
  padding: 1rem 3rem;
  text-transform: uppercase;
  z-index: 2;
}
#hero-feature-subpage.marcom-hero .textTitle h1,
#hero-feature-subpage.marcom-hero .textTitle h2,
#hero-feature-subpage.marcom-hero .textTitle p {
  margin: 0;
  padding: 0;
  font-weight: 700;
  font-size: 2rem;
  line-height: 1.2;
}
/* Grid - hero-subpage-feature */
#hero-feature-subpage.marcom-hero {
  display: grid;
  grid-template-columns: 1fr;
  grid-template-rows: auto;
}
#hero-feature-subpage.marcom-hero .content {
  grid-column: 1;
  grid-row: 2;
  z-index: 2;
  margin-top: -0.7rem;
}
#hero-feature-subpage.marcom-hero picture {
  grid-column: 1;
  grid-row: 1;
  width: 100%;
  height: auto;
}
@media (min-width:800px) {
    #hero-feature-subpage.marcom-hero {
      display: grid; 
      grid-template-columns: 0.1fr auto 1.2fr 0.1fr; 
      grid-template-rows: 1fr 1.6fr 0.4fr; 
      gap: 0px 0px; 
    }
    #hero-feature-subpage.marcom-hero picture { 
        grid-area: 1 / 1 / 4 / 5; 
    }
    #hero-feature-subpage.marcom-hero .content {
        grid-area: 3 / 2 / 4 / 3;
        margin: 0;
        margin-block-end: 0.6rem;
    }
}
/****************************************/
/* Testimonial v01 */
/* Using the Plugin-Card */
/* Use .hp-billboard to style a full-width testimonial (incl blockquote) */
.plugin-card.hp-billboard {
    --cwf-card--background-color: #121212;
    --cwf-card--foreground-color: #f5f5f5;
    background-color: var(--cwf-card--background-color);
    border: 0;
    margin-bottom: 1rem;
    z-index: 0;
}
.hp-billboard .plugin-card__container {
    max-width: 100%;
}
.hp-billboard .plugin-card__image {
    object-fit:cover; 
    width:100%;
    height:100% !important;
}
.hp-billboard .plugin-card__body {
    padding: 5rem 3rem 3rem;
}
@media (min-width:1024px) {
    .hp-billboard .plugin-card__media {
        width:50%;
     }
     .hp-billboard .plugin-card__body {
        padding: 4rem;
        margin-left: 2rem;
    }
}
.hp-billboard blockquote {
    position:relative;
}
.hp-billboard blockquote p:first-child {
    border-bottom: 0;
    border-top: 0;
    font-size: 1.5rem;
    font-style: normal;
    line-height:1.3;
    margin: 0 0 1rem;
}
.hp-billboard blockquote + p {
    font-size: 1rem;
    font-style: italic;
    padding-top: 2rem;
}
.hp-billboard strong {
    color: var(--hp-color--gold);
    font-style: normal;
}
/* Add fancy svg quotes */
.plugin-card.hp-billboard p:only-child:after,
.plugin-card.hp-billboard p:only-child:before {
    content: "";
    font-size: 4em;
    line-height: .3em;
    opacity: .5;
    width: 40px;
    height: 40px !important;
    vertical-align: -.2em;
    padding: 0 0 0 .4em;
}
.plugin-card.hp-billboard p:only-child:before {
    position: absolute;
    z-index: 5;
    left: 0;
    top: -2rem;
    background-position: 0 0 !important;
}
@media (min-width:1024px) {
    .plugin-card.hp-billboard p:only-child:before {
        left: -2.8rem;
        top: -0.2rem;
    }
}
.plugin-card.hp-billboard p:only-child:after {
    background-position: 0 70px !important;
    margin-left: 0.35rem;
    line-height: 0;
}
/* Open quote T4 #621725 */
.plugin-card.hp-billboard p:only-child:before {
    /*background: transparent url('https://www.vcu.edu/media/vcu/assets/images/smart-quotes/smart-quotes-white-open.svg') no-repeat;
    background: transparent url("") no-repeat;*/
    background: url('/media/vcu-homepage/assets/images/smart-quotes-white-open.svg') transparent no-repeat;
}
/* Close quote T4 #621726 */
.plugin-card.hp-billboard p:only-child:after {
    /*background: transparent url('https://www.vcu.edu/media/vcu/assets/images/smart-quotes/smart-quotes-white-close.svg') no-repeat;
    background: transparent url("") no-repeat;*/
    background: url('/media/vcu-homepage/assets/images/smart-quotes-white-close.svg') transparent no-repeat;
}
/* Hide blurred background image */
.plugin-card.hp-billboard .plugin-card__background {
    opacity:0 !important;
    display:none !important;
}
/****************************************/
/* Image List component */
/* Using the Plugin-Card */
/* Adjust default CSS for image list cards */
.plugin-card.hp-image-list {
    border: none; /* remove */
}
.hp-image-list .plugin-card__media {
    border: 5px solid var(--hp-color--gold);
}
.plugin-card.hp-image-list .plugin-card__background {
    webkit-backface-visibility: hidden;
   backface-visibility: hidden;
   display: none !important; /* hide bg image */
}
@media (min-width:1024px) {
    /* Modify image placement and backgrounds */
    .hp-image-list .plugin-card__media {
        align-items: flex-start; /* top aligned */
        background-color: transparent; /* hide black bg */
        width: 35%; /* adjust; override the default 25% width */
    }
    .hp-image-list .plugin-card__image {
        position: relative;
        object-fit: cover; /* Fill the space */
        width: 100%;
        height: 100% !important; /* override style */
    }
}
/****************************************/
/* Callout box v01 */
/* Using the Plugin-Card */
/* Using class .hp-callout */
.hp-callout {
    padding: 0;
    border: none;
}
.hp-callout--basic {
    background: var(--hp-color--offwhite);
    border: 4px solid var(--hp-color--gold);
    padding: 0.5em 2em;
}
@media (min-width: 450px) {
   .hp-callout--basic {
        padding: 0;
    } 
}
@media (min-width:320px) {
   .hp-callout--basic {
        padding: 0;
    } 
}
/****************************************/
/* Stats card v01 */
/* Using the Plugin-Card */
/* With before:{
} */
.hp-stats {
    justify-items:center;
    padding:1rem;
}
/* STATS bars */
.hp-stats .plugin-card {
  background: transparent;
  border:none;
  width: 100%;
}
@media (min-width:1020px) {
  .hp-stats .plugin-card {
    max-width: 350px;
    text-align: center;
  }
}
.hp-stats p,
.hp-stats a {
    font-size: 1rem;
    font-weight:normal;
}
.hp-stats a:focus,
.hp-stats a:hover {
    text-decoration:none;
}
.hp-stats strong {
    display: block;
    font-family: 'thunderhouse-pro';
    font-weight: 400 !important;
    font-size: 9rem !important;
    padding: 0 0 1rem;;
    margin: 0;
    line-height: 0.8 !important;
}
.hp-stats span {
    font-size: normal;
    vertical-align: middle !important;
}
/****************************************/
/*** Stats List v01 ***/
/* Example on 'Get Involved' page */
/* Stats in a flexbox grid : using list items in a UL */
/* Apply css:{.hp-stats-list} to the T4 content name */
/* Big number gets  */
.hp-stats-list .plugin-card__container {
    padding: 1.5rem;
}
.hp-stats-list h3 {
    text-align: center;
    font-size: 1.5rem;
    margin: 0;
    padding: 0;
    padding-bottom: 2rem;
    color: var(--hp-color--offblack)
}
.hp-stats-list ul {
    display: flex;
    list-style: none;
    flex-wrap: wrap;
    padding: 0;
    margin: 0;
    gap: 6rem;
    justify-content: center;
    -webkit-align-items: center;
}
.hp-stats-list > * {
    flex: 1 1 0;
}
.hp-stats-list li {
    text-align: center;
}
.hp-stats-list li strong {
    font-family: Roboto,sans-serif;
    font-size: 4rem !important;
    margin: 0;
    line-height: 0.8 !important;
    display: block;
}
.hp-stats-list li span {
    font-size: 1rem;
    vertical-align: middle !important;
}
.hp-stats-list--gold {
    background: var(--hp-color--gold);
}
/****************************************/
/* Hover Card style v01 */
/* Title and photo visible */
/* On hover:  Blurb and title animate in view */
/* Add .cwf-grid.hp-card-reveal to parent div */ 
/* plugin- cards are child elements */
.cwf-grid.hp-card-reveal {
  grid-gap:.5rem;
  padding-bottom: 1.5rem;
}
.hp-card-reveal .plugin-card__container {
    position:relative;
    overflow:hidden;
}
.hp-card-reveal .plugin-card__body {
    position: absolute;
    bottom: 0;
    z-index: 2;
    width: 100%;
    height: auto;
    margin: 0;
    padding: 0;
}
.hp-card-reveal .plugin-card__body > h2,
.hp-card-reveal .plugin-card__body > h3,
.hp-card-reveal .plugin-card__body p {
    -webkit-transition: opacity .35s, -webkit-transform .35s;
    transition: opacity .35s, -webkit-transform .35s;
    -o-transition: opacity .35s, transform .35s;
    transition: opacity .35s, transform .35s;
    transition: opacity .35s, transform .35s, -webkit-transform .35s;
}
.hp-card-reveal .plugin-card__body > h2,
.hp-card-reveal .plugin-card__body > h3 {
    position: absolute;
    vertical-align: baseline;
    bottom: 0;
    color: #fff;
    font-size: 1rem;
    padding: 1.5rem !important;
    margin: 0;
    width: 100%;
    height: auto;
    text-transform: uppercase;
    background: rgb(2,0,36);
    background: linear-gradient(90deg, rgba(0,0,0,.8) 0%, rgba(20,20,20,.75) 35%, rgba(34,34,34,.75) 100%);
}
.hp-card-reveal .plugin-card__body a {
    text-decoration: none;
}
.hp-card-reveal .plugin-card__body p {
    background: #000;
    width: 100%;
    color: #fff;
    text-transform: none;
    opacity: 0;
    -webkit-transform: translate3d(0, 50px, 0);
    transform: translate3d(0, 50px, 0);
    font-size: 1rem;
    padding: 1.5rem !important;
    line-height: 1.5;
}
.hp-card-reveal .plugin-card__background {
    display:none;
}
.hp-card-reveal .plugin-card__container:hover img,
.hp-card-reveal .plugin-card:focus img {
    opacity: .3;
}
/* Transform on hover & focus */
.hp-card-reveal .plugin-card:focus h2, 
.hp-card-reveal .plugin-card__container:hover h2,
.hp-card-reveal .plugin-card:focus h3, 
.hp-card-reveal .plugin-card__container:hover h3 {
    background: 0 0;
    -webkit-transform: translate3d(0, -200px, 0);
    transform: translate3d(0, -200px, 0);
}
.hp-card-reveal .plugin-card__container:hover p,
.hp-card-reveal .plugin-card__container:focus p,
.hp-card-reveal .plugin-card:focus p {
    opacity: 1;
    -webkit-transform: translate3d(0, 0, 0);
    transform: translate3d(0, 0, 0);
}
/* Card link */
.hp-card-reveal a,
.hp-card-reveal a:link {
color: #ffb300 !important;
font-weight: 700 !important;
line-height: 1 !important;
text-transform: uppercase !important;
border: 0 !important;
padding: 0 !important;
margin: 10px 0 0 !important;
}
.hp-card-reveal a:hover,
.hp-card-reveal a:focus {
color: #e57200;
}
.hp-card-reveal a:after {
position: relative;
font-family: FontAwesome;
font-weight: 400;
font-size: 16px;
color: #e57200;
content: "\f105";
display: inline-block;
margin: 0;
padding: 0;
  padding-left: 0px;
line-height: 1;
padding-left: 8px;
top: -1px;
vertical-align: middle;
}
.hp-card-reveal a::after {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  content: ' ';
}
/****************************************/
/*! Card styles VCU Homepage 
 * Overrides and additions to Plugin - Card v2.4.0 | Compass v1.4.0 
 * */
/* Plugin card themes */
#cwf-content .plugin-card--black .plugin-card__body a:active,
#cwf-content .plugin-card--black .plugin-card__body a:hover,
#cwf-content .plugin-card--black .plugin-card__body a:focus {
   color: var(--hp-color--black);
}
.plugin-card--gray {
    --cwf-card--background-color: #ebebeb;
    --cwf-card--foreground-color: #333;
    --cwf-card--marker-color: #ffb300;
    --cwf-card--link-color: #006894
}
.plugin-card--gray .plugin-card__body .cwf-button,.plugin-card--gray .plugin-card__body .t4_button {
    --cwf-button--background-color: #fff;
    --cwf-button--border-color: #757575;
    --cwf-button--color: #006894;
    --cwf-button--active--background-color: #0093d2;
    --cwf-button--active--color: #333;
    --cwf-button--hover-focus--background-color: #ffb300;
    --cwf-button--hover-focus--color: #333
}
/* Black theme */
.plugin-card--black {
    --cwf-card--background-color: #121212;
    --cwf-card--foreground-color: #f5f5f5;
    --cwf-link--color: #ffb300;
    --cwf-link--active--color: #fff;
}
.plugin-card--black .plugin-card__body .cwf-button,
.plugin-card--black .plugin-card__body .t4_button {
    --cwf-button--background-color: #121212;
    --cwf-button--border-color: ##ffb300;
    --cwf-button--color: #fff;
