// site JS debug status
const hpDebug = false;
// helper class to control console logging
class Logger {
    constructor() {
        const urlParams = new URLSearchParams(window.location.search);
        this.enabled = ((typeof hpDebug === 'undefined') ? false : hpDebug) || 
            (urlParams.get('debug') === 'true');
    }
    log(...objs) {
        if (this.enabled && console) {
            console.log(...objs);
        }
    }//log
    error(...objs) {
        if (this.enabled && console) {
            console.error(...objs);
        }
    }
}//class

/*
	ClickCards
	Make the entire plugin-card clickable, linking to the first link contained 
	within the card. If no link exists, the click event is not applied. 
*/
class ClickCards {
	static defaults = {
		selector: '.plugin-card'
	};
	constructor(options) {
		this.options = { ...this.constructor.defaults, ...options };
		this.init();
	}//constructor
	init() {
		const _this = this;
		let cards = document.querySelectorAll(_this.options.selector);
		if (cards) {
			cards.forEach((card, index) => {
				const id = `pCard${index}`;
				const link = card.querySelector('a');
				if (link) {
					card.addEventListener('click', function() {
						location = link.href;
					});
					card.addEventListener('keydown', evt => {
						if (evt.key == 'Enter') {
							location = evt.target.dataset.href;
						}
					});
					card.style.cursor = 'pointer';
					card.tabIndex = 0;
					card.setAttribute('role', 'link');
					card.dataset.href = link.href;
					//card.setAttribute('aria-labelledby', `#${id}`);
					link.tabIndex = -1;
					link.id = id;
				}
			});
		}
	}
	static initCards(options) {
		return new ClickCards(options);
	}
}//class 
//window.addEventListener('load', function() {
//    ClickCards.initCards({selector: '.hp-card--click-cards .plugin-card'});
//});
window.addEventListener('load', function() {
    function loadClickCards(selector) {
        if (document.querySelector(selector)) {
            ClickCards.initCards({ selector: selector });
        }
    }
    loadClickCards('.hp-card--click-cards .plugin-card');
    loadClickCards('.hp-click .plugin-card');
    loadClickCards('.hp-card-reveal .plugin-card');
});

const shortNavHoverMode = 1;
class navFullMenu {
  constructor({ prefix = "hp", animations = 1 } = {}) {
