    // find all links/buttons in the full menu
    this.navFullMenuLinks = this.navFullMenu.querySelectorAll("a, button");
    // create varialbe to hold event handlers so it can be registered and un-registered
    this.handlers = {};
    // trapFocus - keep focus on the full menu as users tab through
    // the links.
    this.handlers.trapFocus = function (evt) {
      _this.logger.log("[Full nav]  trapFocus", evt);
      if (evt.key == "Tab") {
        if (_this.navFullMenuLinks) {
          _this.logger.log("[Full nav] links", _this.navFullMenuLinks);
          if (evt.shiftKey) {
            if (evt.target == _this.navFullMenuLinks[0]) {
              evt.preventDefault();
              _this.navFullMenuLinks[_this.navFullMenuLinks.length - 1].focus();
            }
          } else {
            if (
              evt.target ==
              _this.navFullMenuLinks[_this.navFullMenuLinks.length - 1]
            ) {
              evt.preventDefault();
              _this.navFullMenuLinks[0].focus();
            }
          }
        }
      }
    };
    this.handlers.externalClick = function (evt) {
      if (!_this.isDescendent(evt.target, _this.navShortMenu)) {
        _this.closeActiveShortMenu();
      }
    };
    // We'll update this keyboard navigation
    document.body.addEventListener("keydown", (event) => {
      // _this.logger.log("keydown");
      if (event.isComposing || event.keyCode === 229) {
        return;
      }
      if (this.visible && event.code === "Escape") {
        _this.hideFullMenu();
      }
    });
    this.toggleFullMenu.addEventListener("click", (event) => {
      if (!this.visible) {
        _this.showFullMenu();
      } else {
        _this.hideFullMenu();
      }
    });
    this.toggleFullMenuClose.addEventListener("click", (event) => {
      if (this.visible) {
        _this.hideFullMenu();
      }
    });
    // ACTION MENU
    _this.navActionMenu.addEventListener("keyup", function (evt) {
      if (evt.key == "Tab") {
        _this.closeActiveShortMenu();
      }
    });
    // Quicklinks menu
    // check for existence since we may or may not have it
    // since quick menu also has search icon, we need to
    // go and get the actual quicklinks section that's
    // inside of the quick menu
    if (
      this.toggleQuickMenu &&
      this.toggleQuickMenu?.nextElementSibling.tagName === "UL"
    ) {
      _this.logger.log("inside quick toggle");
      let quicklinksList = this.toggleQuickMenu.nextElementSibling;
      this.toggleQuickMenu.addEventListener("click", (event) => {
        if (!this.visible) {
          quicklinksList.classList.remove(
            "hp-nav--quicklinks--hidden",
            "animating"
          );
          quicklinksList.classList.add("hp-nav--quicklinks--visible");
          if (this.properties.animations) {
            animationStart(quicklinksList);
          }
          this.visible = 1;
          // document.body.style.overflowY = "hidden";
        } else {
          quicklinksList.classList.remove(
            "hp-nav--quicklinks--visible",
            "animating"
          );
          quicklinksList.classList.add("hp-nav--quicklinks--hidden");
          if (this.properties.animations) {
            animationStart(quicklinksList);
          }
          this.visible = 0;
          // document.body.style.overflowY = "auto";
        }
      });
    }
    // Search menu begin (open full menu)
    this.toggleSearchMenu.addEventListener("click", (event) => {
      if (!this.visible) {
        _this.showFullMenu();
        this.navSearchInput.focus();
        _this.fullMenuSrc = event.target;
      } else {
        _this.hideFullMenu();
      }
