    //       animationStart(this.navSearchMenu);
    //     }
    //     this.visible = 0;
    //     // document.body.style.overflowY = "auto";
    //   }
    // });
    // Search menu end (in its own modal)
    // make this more specific if necessary
    const navList = this.navFullMenu.querySelector(".hp-nav__list--level-1");
    const navListResult = [];
    // _this.logger.log(navList);
    for (const parentItem of navList.children) {
      const parentTitle = parentItem.querySelector("a")?.textContent.trim();
      const childrenList = parentItem.querySelector("ul");
      const parentUrl = parentItem.querySelector("a")?.href.trim();
      // _this.logger.log(parentTitle);
      // _this.logger.log(parentUrl);
      // _this.logger.log(childrenList);
      if (childrenList) {
        const children = [];
        for (const childItem of childrenList.children) {
          const childTitle = childItem.querySelector("a")?.textContent.trim();
          const childUrl = childItem.querySelector("a")?.href.trim();
          // _this.logger.log(childTitle);
          // _this.logger.log(childUrl);
          children.push({ title: childTitle, url: childUrl });
        }
        navListResult.push({ title: parentTitle, url: parentUrl, children });
      } else {
        navListResult.push({ title: parentTitle, url: parentUrl });
      }
    }
    this.json = navListResult;
    this.addChildrenToShortNav();
    // Short menu
    // event listener to set active item
    /* this block can be removed if the focus/blue event handlers works
    _this.navShortMenu.addEventListener('focus', evt => {
      if (evt.key == 'Tab') {
        _this.setActiveShortMenuItem(evt.target);
      }
