    _this.shortMenuHoverOverride = false;
    _this.shortMenuFocusLink = null;
    const shortMenuLinks = _this.navShortMenu.querySelectorAll("a");
    if (shortMenuLinks) {
      _this.toHideActiveShortMenu = null;
      shortMenuLinks.forEach((link) => {
        link.addEventListener("focus", (evt) => {
          if (!_this.shortMenuHoverOverride) {
