  hideFullMenu() {
    const _this = this;
    this.navFullMenu.classList.remove(
      "hp-nav--full-menu--visible",
      "animating"
    );
    this.navFullMenu.classList.add("hp-nav--full-menu--hidden");
    if (this.properties.animations) {
      animationStart(this.navFullMenu);
    }
    this.visible = 0;
    // document.body.style.overflowY = "auto";
    // _this.logger.log("escape while full menu is open");
    document.body.classList.remove("hp-nav--show-full-menu");
    // return focus back to the full menu toggle
    if (this.fullMenuSrc) {
      this.fullMenuSrc.focus();
      delete this.fullMenuSrc;
    } else {
      this.toggleFullMenu.focus();
    }
    // unregister event handler to trap focus
    this.navFullMenu.removeEventListener("keydown", _this.handlers.trapFocus);
  } //hideFullMenu
  setActiveShortMenuItem(elem) {
    const _this = this;
    _this.logger.log("[navFullMenu] setActiveShortMenuItem - elem", elem);
    // get top level short menu links
    const items = _this.navShortMenu.querySelectorAll(
      ".hp-nav__list--level-1 > .hp-nav__item > a"
    );
    if (items) {
      _this.logger.log("[navFullMenu] setActiveShortMenuItem - items", items);
      let hasActive = false;
      items.forEach((item, index) => {
        const isDescendent = _this.isDescendent(elem, item.parentElement);
        _this.logger.log("[navFullMenu] isDescendent", isDescendent);
        if (item == elem || isDescendent) {
          item.parentElement.classList.add("hp-nav__item--active");
          hasActive = true;
        } else {
          item.parentElement.classList.remove("hp-nav__item--active");
        }
      });
      if (hasActive) {
        //document.addEventListener('click', _this.handlers.externalClick);
      }
    }
