// Set the toggle's "aria-expanded" attribute
function toggleExpanded(toggle) {
  // If there's no toggle, do nothing else...
  if (!toggle) return;
  // ... otherwise, set its "aria-expanded" attribute to the opposite value
  let newState = 0;
  if (toggle.getAttribute("aria-expanded") === "true") {
    newState = false;
  } else {
    newState = true;
  }
  toggle.setAttribute("aria-expanded", newState);
  toggle.parentElement.setAttribute("aria-expanded", newState);
}
