// Find the actionLink in .hp-nav--action
const actionLinks = document.querySelectorAll(".hp-nav--action a");
if (actionLinks && actionLinks.length) {
  Array.from(actionLinks).forEach((actionLink, index) => {
    // Check if the actionLink exists and its text is "myVCU"
    if (actionLink && actionLink.textContent.trim() === "myVCU") {
      const actionLinkText = actionLink.textContent.trim();
      // Wrap the first two characters in a  element
      const modifiedText = `${actionLinkText.substring(
        0,
        2
      )}${actionLinkText.substring(2)}`;
      // Update the actionLink's HTML content
      actionLink.innerHTML = modifiedText;
    }
  });
}

// Move section title above sub nav. we'll likely want to
// to duplicate the compass sub nav and work this into there
// to help minimize content shifts.
var breadcrumbNavElement = document.querySelector(".cwf-breadcrumb__list");
if (breadcrumbNavElement) {
  var breadcrumbNavItems = Array.from(breadcrumbNavElement?.children);
  var breadcrumbTarget = breadcrumbNavItems[1];
  console.log(breadcrumbTarget);
  var subNavElement = document.querySelector(".cwf-nav--sub");
  var firstSubNavItem = document.querySelector(".cwf-nav--sub")?.querySelector(".cwf-nav__item");
  if (firstSubNavItem) {
    var newSubNavItem = document.createElement("li");
    newSubNavItem.classList.add("cwf-nav__item");
    // check if item has children
    var newSubNavLink = document.createElement("a");
    newSubNavLink.classList.add("cwf-nav__link");
    if (breadcrumbTarget.childElementCount) {
      console.log('breadcrumbTarget, has link: ', breadcrumbTarget.firstElementChild);
      newSubNavLink.innerText = breadcrumbTarget.firstElementChild.innerText;
      newSubNavLink.href = breadcrumbTarget.firstElementChild.pathname;
    } else {
      newSubNavLink.innerText = breadcrumbTarget.innerText;
      newSubNavLink.href = "#cwf-content";
      newSubNavItem.classList.add("cwf-nav__item--is-current");
      console.log('breadcrumbTarget, no link: ', breadcrumbTarget);
    }
    newSubNavItem.appendChild(newSubNavLink);
    firstSubNavItem.parentNode.insertBefore(newSubNavItem, firstSubNavItem);
  }
}

